import React, { useState } from 'react';

function Bill(props) {
    const [bill, setNewBill] = useState('');

    function setBill(e) {
        const newBill = e.target.value;
        setNewBill(newBill);
        props.addBill();
    }
    return (
        <div>
            <span>Bill</span>
            <input value={bill} onChange={setBill} type="text" />
        </div>
    );
}

export default Bill;
