import React, { useState } from 'react';
// import Buttons from './buttons';
const isError = false;

function Tip(props) {
    const [bill, setNewBill] = useState('');
    const [custom, setCustom] = useState('');
    const [people, setPeople] = useState('');

    const tips = [5, 10, 15, 25, 50];

    function setTip(e) {
        const customInput = e.target.value;
        setCustom(customInput);
    }

    function setBill(e) {
        const newBill = e.target.value;
        setNewBill(newBill);
    }

    function setNewPeople(e) {
        const newPeople = e.target.value;
        setPeople(newPeople);
    }

    return (
        <div>
            <div className="input-container">
                <div className='labels'>
                    <span className='dark-grayish'>Bill</span>
                    <span className={isError ? 'error' : 'no-error'} >Can't be zero</span>
                </div>
                <input value={bill} onChange={setBill} type="text" placeholder='0' />
            </div>

            <div className='input-container'>
                <div className='labels'>
                    <span className='dark-grayish'>Number of People</span>
                    <span className={isError ? 'error' : 'no-error'} >Can't be zero</span>
                </div>
                <input type="text" onChange={setNewPeople} value={people} placeholder='0' />
            </div>
        </div>
    );
}

export default Tip;
