import React, { useState } from 'react';

function People() {
    const [noPeople, setNoPeople] = useState('');

    function setPeople(e) {
        const newNumberOfPeople = e.target.people;
        setNoPeople(newNumberOfPeople);
    }
    return (
        <>
            <span>Number of People</span>
            <input type="text" value={noPeople} onChange={setNoPeople} />
        </>
    );
}

export default People;
