import React, { useState } from 'react';
// import Buttons from './buttons';

function Tip(props) {
    const [bill, setNewBill] = useState('');
    const [custom, setCustom] = useState('');
    const [people, setPeople] = useState('');

    const tips = [5, 10, 15, 25, 50];

    function setTip(e) {
        const customInput = e.target.value;
        setCustom(customInput);
        props.onChange(custom)
    }

    function setBill(e) {
        const newBill = e.target.value;
        setNewBill(newBill);
    }

    function setNewPeople(e) {
        const newPeople = e.target.value;
        setPeople(newPeople);
    }

    return (
        <div>
            <span>Bill</span>
            <input value={bill} onChange={setBill} type="text" />
            <span>Select Tip %</span>
            {tips.map((tip, index) => {
                return <button value={tip} key={index} onClick={() => {
                    props.onClick(tip, bill, people)
                }} >{tip}</button>
            })}
            <input onChange={setTip} value={custom} placeholder="Custom" type="text" />
            <span>Number of People</span>
            <span>Can't be zero</span>
            <input type="text" onChange={setNewPeople} value={people} />
        </div>
    );
}

export default Tip;
